import React, {useState, useEffect} from 'react';
import { Text, View, StyleSheet, Image, TextInput, TouchableOpacity } from 'react-native';
import { auth } from '../../config/firebaseconfig';
import { signOut } from 'firebase/auth';
import { db } from '../../config/firebaseconfig';
import { collection, addDoc } from 'firebase/firestore';
import { Table, Button } from "react-bootstrap";
import CelularDataService from "../../services/celular.services";
import { Container, Navbar, Row, Col } from "react-bootstrap";
import AdicionarCelular from "./AdicionarCelular";
import ListaCelular from "./ListaCelular";
import "./"


export default function AvaliacaoCelular({navigation}) {
  const [celularId, setCelularId] = useState("");

  const getCelularIdHandler = (id) => {
    console.log("The ID of documento to be edited: ", id);
    setCelularId(id);
  };

  //Front
  return (
    <>
      <Navbar bg="dark" variant="dark" className="header">
        <Container>
          <Navbar.Brand href="#home">Library - Firebase CRUD</Navbar.Brand>
        </Container>
      </Navbar>

      <Container style={{ width: "400px" }}>
        <Row>
          <Col>
            <AdicionarCelular id={celularId} setCelularId={setCelularId} />
          </Col>
        </Row>
      </Container>
      <Container>
        <Row>
          <Col>
            <ListaCelular getCelularId={getCelularIdHandler} />
          </Col>
        </Row>
      </Container>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
  },
  logo: {
    width: 300, 
    height: 200, 
    marginTop: 20
  },
  texto: {
    color: 'white',
    fontWeight: "bold",
    fontSize: 25,
    lineHeight: 30,
    marginTop: 20,
    resizeMode: 'contain'
  },
  TextInput: {
    marginTop: 5,
    width: '90%',
    height: 40,
    backgroundColor: 'white',
    borderRadius: 20,
    paddingLeft: 10,
    marginBottom: 10
  },
  btnLogout:{
    width: '50%',
    height: 40,
    backgroundColor: '#0FA3FF',
    borderRadius: 20,
    justifyContent: 'center',
    marginTop: 30
  },
  btnText: {
    color: 'white',
    textAlign: 'center',
    fontWeight: "bold"
  }
});
