import React, { useState, useEffect } from "react";
import { Table, Button } from "react-bootstrap";
import CelularDataService from "./../../services/celular.services";

const ListaCelular = ({ getCelularId }) => {
    const [celular, setCelular] = useState([]);
    useEffect(() => {
      getCelular();
    }, []);
  
    const getCelular = async () => {
      const data = await CelularDataService.getAllCelular();
      console.log(data.docs);
      setBooks(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };
  
    const deleteHandler = async (id) => {
      await CelularDataService.deleteCelular(id);
      getCelular();
    };
    return (
      <>
        <div className="mb-2">
          <Button variant="dark edit" onClick={getCelular}>
            Refresh List
          </Button>
        </div>
  
        {/* <pre>{JSON.stringify(books, undefined, 2)}</pre>} */}
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>#</th>
              <th>Marca Celular</th>
              <th>Modelo Celular</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {celular.map((doc, index) => {
              return (
                <tr key={doc.id}>
                  <td>{index + 1}</td>
                  <td>{doc.marca}</td>
                  <td>{doc.modelo}</td>
                  <td>{doc.status}</td>
                  <td>
                    <Button
                      variant="secondary"
                      className="edit"
                      onClick={(e) => getCelularId(doc.id)}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="danger"
                      className="delete"
                      onClick={(e) => deleteHandler(doc.id)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </>
    );
  };

export default ListaCelular;
