import React, {useState, useEffect} from 'react';
import { Text, View, StyleSheet, Image, TextInput, TouchableOpacity, Button, ScrollView } from 'react-native';
import { ListItem, Avatar } from "react-native-elements";

import firebase from "../../../config/firebaseconfig"

const AvaliacaoCelular = () => {
return (
  <ScrollView>
  <View style={styles.inputGroup}>
      <TextInput placeholder='Marca' />
  </View>

  <View style={styles.inputGroup}>
      <TextInput placeholder='Modelo' />
  </View>

  <View style={styles.inputGroup}>
      <TextInput placeholder='Status' />
  </View>

  <View>
    <Button title="Salvar" />
  </View>
  </ScrollView>
);
}

export default AvaliacaoCelular ;

const styles = StyleSheet.create({
  inputGroup: {
    flex: 1,
    padding: 0,
    marginBottom: 15,
    borderBottomWidth: 1,
    borderBottomColor: "#cccccc",
  },
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
  },
  logo: {
    width: 300, 
    height: 200, 
    marginTop: 20
  },
  texto: {
    color: 'white',
    fontWeight: "bold",
    fontSize: 25,
    lineHeight: 30,
    marginTop: 20,
    resizeMode: 'contain'
  },
  TextInput: {
    marginTop: 5,
    width: '90%',
    height: 40,
    backgroundColor: 'white',
    borderRadius: 20,
    paddingLeft: 10,
    marginBottom: 10
  },
  btnLogout:{
    width: '50%',
    height: 40,
    backgroundColor: '#0FA3FF',
    borderRadius: 20,
    justifyContent: 'center',
    marginTop: 30
  },
  btnText: {
    color: 'white',
    textAlign: 'center',
    fontWeight: "bold"
  }
});
