import React, { useState, useEffect } from "react";
import { Form, Alert, InputGroup, Button, ButtonGroup } from "react-bootstrap";
import CelularDataService from "./../../services/celular.services";

const AdicionarCelular = ({ id, setCelularId }) => {
    const [marca, setMarca] = useState("");
    const [modelo, setModelo] = useState("");
    const [status, setStatus] = useState("Novo");
    const [flag, setFlag] = useState(true);
    const [message, setMessage] = useState({ error: false, msg: "" });
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      setMessage("");
      if (marca === "" || modelo === "") {
        setMessage({ error: true, msg: "Todos os campos são obrigatorios" });
        return;
      }
      const newCelular = {
        marca,
        modelo,
        status,
      };
      console.log(newCelular);
  
      try {
        if (id !== undefined && id !== "") {
          await CelularDataService.updateCelular(id, newBook);
          setBookId("");
          setMessage({ error: false, msg: "Updated successfully!" });
        } else {
          await BookDataService.addBooks(newBook);
          setMessage({ error: false, msg: "New Book added successfully!" });
        }
      } catch (err) {
        setMessage({ error: true, msg: err.message });
      }
  
      setTitle("");
      setAuthor("");
    };
  
    const editHandler = async () => {
      setMessage("");
      try {
        const docSnap = await BookDataService.getBook(id);
        console.log("the record is :", docSnap.data());
        setTitle(docSnap.data().title);
        setAuthor(docSnap.data().author);
        setStatus(docSnap.data().status);
      } catch (err) {
        setMessage({ error: true, msg: err.message });
      }
    };
  
    useEffect(() => {
      console.log("The id here is : ", id);
      if (id !== undefined && id !== "") {
        editHandler();
      }
    }, [id]);
    return (
      <>
        <div className="p-4 box">
          {message?.msg && (
            <Alert
              variant={message?.error ? "danger" : "success"}
              dismissible
              onClose={() => setMessage("")}
            >
              {message?.msg}
            </Alert>
          )}
  
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formBookTitle">
              <InputGroup>
                <InputGroup.Text id="formBookTitle">B</InputGroup.Text>
                <Form.Control
                  type="text"
                  placeholder="Book Title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                />
              </InputGroup>
            </Form.Group>
  
            <Form.Group className="mb-3" controlId="formBookAuthor">
              <InputGroup>
                <InputGroup.Text id="formBookAuthor">A</InputGroup.Text>
                <Form.Control
                  type="text"
                  placeholder="Book Author"
                  value={author}
                  onChange={(e) => setAuthor(e.target.value)}
                />
              </InputGroup>
            </Form.Group>
            <ButtonGroup aria-label="Basic example" className="mb-3">
              <Button
                disabled={flag}
                variant="success"
                onClick={(e) => {
                  setStatus("Available");
                  setFlag(true);
                }}
              >
                Available
              </Button>
              <Button
                variant="danger"
                disabled={!flag}
                onClick={(e) => {
                  setStatus("Not Available");
                  setFlag(false);
                }}
              >
                Not Available
              </Button>
            </ButtonGroup>
            <div className="d-grid gap-2">
              <Button variant="primary" type="Submit">
                Add/ Update
              </Button>
            </div>
          </Form>
        </div>
      </>
    );
  };

export default AdicionarCelular;
