import { db } from "../config/firebaseconfig";

import { collection, getDocs, getDoc, addDoc, updateDoc, deleteDoc, doc } from "firebase/firestore";

const celularCollectionRef = collection(db, "celular")
class CelularDataService{
    addCelular = (newCelular) => {
        return addDoc(celularCollectionRef, newCelular);
    };

    updateCelular = (id, updateCelular) => {
        const celularDoc = doc(db, "celular", id);
        return updateDoc(celularDoc, updateCelular);
    };

    deleteCelular = (id) => {
        const celularDoc = doc(db, "celular", id);
        return deleteDoc(celularDoc);
    }

    getAllCelular = () => {
        return getDocs(celularCollectionRef);
    }

    getCelular = (id) => {
        const celularDoc = doc(db, "celular", id);
        return getDocs(celularDoc);
    }
}

export default new CelularDataService();
